numberWords[n_] := Module[{units, teens, tens, hundreds, thousands, num, words},
  units = {"", "one", "two", "three", "four", "five", "six", "seven", "eight", "nine"};
  teens = {"ten", "eleven", "twelve", "thirteen", "fourteen", "fifteen", "sixteen", "seventeen", "eighteen", "nineteen"};
  tens = {"", "", "twenty", "thirty", "forty", "fifty", "sixty", "seventy", "eighty", "ninety"};
  hundreds = "hundred";
  thousands = "thousand";
  num = IntegerDigits[n];
  words = "";
  Switch[Length[num],
    4, words = units[[num[[1]] + 1]] <> thousands,
    3, words = units[[num[[1]] + 1]] <> hundreds;
         If[Mod[n, 100] != 0, words = words <> "and"],
    2, Null,
    1, Null
  ];
  If[Length[num] >= 2 && num[[-2]] == 1,
    words = words <> teens[[num[[-1]] + 1]],
    If[Length[num] >= 2, words = words <> tens[[num[[-2]] + 1]]];
    words = words <> units[[num[[-1]] + 1]]
  ];
  words
];
Total[StringLength[numberWords[#]] & /@ Range[1, 1000]]

