ReplaceablePrimeFamily[n_Integer, familySize_Integer] := Module[
  {digits, positions, replacements, primes, candidate},
  digits = IntegerDigits[n];
  positions = Subsets[Range[Length[digits]], {1, Length[digits] - 1}];
  For[i = 1, i <= Length[positions], i++,
   replacements = Table[
     candidate = ReplacePart[digits, Thread[positions[[i]] -> d]];
     If[First[candidate] != 0, FromDigits[candidate], Nothing],
     {d, 0, 9}
     ];
   primes = Select[replacements, PrimeQ];
   If[Length[primes] >= familySize, Return[primes[[1]]]]
   ];
  None
  ];

FindSmallestPrimeWithFamily[familySize_Integer] := Module[
  {n = 10, result},
  While[True,
   n = NextPrime[n];
   result = ReplaceablePrimeFamily[n, familySize];
   If[result =!= None, Return[result]]
   ]
  ];

