ConcatenatedPrimeQ[a_, b_] := Module[{n1, n2},
  n1 = FromDigits[Join[IntegerDigits[a], IntegerDigits[b]]];
  n2 = FromDigits[Join[IntegerDigits[b], IntegerDigits[a]]];
  PrimeQ[n1] && PrimeQ[n2]
]

FindPrimeSet[n_] := Module[{primes, pairs, checkSet, result},
  primes = Prime[Range[2, 2000]];
  pairs = Select[Subsets[primes, {2}], ConcatenatedPrimeQ @@ # &];
  checkSet[set_] := AllTrue[Subsets[set, {2}], ConcatenatedPrimeQ @@ # &];
  result = SelectFirst[Subsets[primes, {n}], checkSet];
  If[result === Missing["NotFound"], "Not found", Total[result]]
]

