n = Length[matrix];
dijkstra[grid_] := Module[{dist, visited, pq, neighbors, update},
  dist = ConstantArray[Infinity, Dimensions[grid]];
  dist[[1, 1]] = grid[[1, 1]];
  visited = ConstantArray[False, Dimensions[grid]];
  pq = PriorityQueue[{{1, 1} -> grid[[1, 1]]}];
  neighbors[{i_, j_}] := Select[{{i + 1, j}, {i - 1, j}, {i, j + 1}, {i, j - 1}}, 1 <= #[[1]] <= n && 1 <= #[[2]] <= n &];
  update[{i_, j_}, d_] := If[! visited[[i, j]] && d < dist[[i, j]], dist[[i, j]] = d; Enqueue[pq, {i, j} -> d]];
  While[Length[pq] > 0,
   With[{current = First[First[pq]]},
    Dequeue[pq];
    If[! visited[[current[[1]], current[[2]]]],
     visited[[current[[1]], current[[2]]]] = True;
     Do[update[neigh, dist[[current[[1]], current[[2]]]] + grid[[neigh[[1]], neigh[[2]]]]], {neigh, neighbors[current]}];
     ]
    ]
   ];
  dist[[n, n]]
  ];
