SeedRandom[1];
numSimulations = 10^6;
boardSize = 40;
communityChest = {2, 17, 33};
chance = {7, 22, 36};
goToJail = 30;
jail = 10;
communityChestCards = {0, jail};
chanceCards = {0, jail, 11, 24, 39, 5, "nextR", "nextR", "nextU", -3};
rollDice[] := RandomInteger[{1, 4}, 2];
nextRailroad[pos_] := Which[
  pos < 5, 5,
  pos < 15, 15,
  pos < 25, 25,
  pos < 35, 35,
  True, 5
];
nextUtility[pos_] := Which[
  pos < 12, 12,
  pos < 28, 28,
  True, 12
];
movePlayer[pos_, roll_] := Module[{newPos = Mod[pos + Total[roll], boardSize]},
  Which[
    MemberQ[communityChest, newPos],
    newPos = RandomChoice[Append[communityChestCards, newPos]],
    MemberQ[chance, newPos],
    newPos = RandomChoice[Append[chanceCards /. {"nextR" -> nextRailroad[newPos], "nextU" -> nextUtility[newPos]}, newPos]],
    newPos == goToJail, newPos = jail
  ];
  newPos
];
simulateGame[] := Module[{position = 0, counts = ConstantArray[0, boardSize], doubles = 0, roll},
  Do[
    roll = rollDice[];
    If[roll[[1]] == roll[[2]], doubles++, doubles = 0];
    If[doubles == 3, position = jail; doubles = 0, position = movePlayer[position, roll]];
    counts[[position + 1]]++,
    {numSimulations}
  ];
  counts
];
counts = simulateGame[];
Ordering[counts, -3]

