ClearAll[findMinimalProductSumNumbers];
findMinimalProductSumNumbers[maxK_] := Module[
  {minProductSum, update, search, maxN, result},
  maxN = 2 maxK;
  minProductSum = ConstantArray[Infinity, maxK];
  update[n_, k_] := If[k <= maxK, minProductSum[[k]] = Min[minProductSum[[k]], n]];
  search[n_, prod_, sum_, start_] := Module[{k},
    k = n - sum + prod;
    If[k <= maxK, update[prod, k]];
    Do[
      If[prod * i > maxN, Break[]];
      search[n + 1, prod * i, sum + i, i],
      {i, start, maxN}
    ];
  ];
  search[0, 1, 0, 2];
  result = Total[DeleteDuplicates[Select[minProductSum, # < Infinity &]]];
  result
];
