solveSudoku[grid_] := Module[{empty, possibilities, solve},
  empty = Position[grid, 0];
  possibilities[pos_] := Complement[Range[9], 
    Union[grid[[pos[[1]], All]], grid[[All, pos[[2]]]], 
      Flatten[grid[[3 Quotient[pos[[1]] - 1, 3] + 1 ;; 3 Quotient[pos[[1]] - 1, 3] + 3, 
        3 Quotient[pos[[2]] - 1, 3] + 1 ;; 3 Quotient[pos[[2]] - 1, 3] + 3]]]]];
  solve[grid_, {}] := grid;
  solve[grid_, {pos_, rest___}] := 
   Module[{p = possibilities[pos]}, 
    If[Length[p] == 0, Nothing, 
     solve[ReplacePart[grid, pos -> #], {rest}] & /@ p]];
  solve[grid, empty]
];

