ClearAll[isSpecialSumSet, findOptimumSet]
isSpecialSumSet[set_] := Module[{subsets, sums, len},
  subsets = Subsets[set];
  sums = Total /@ subsets;
  len = Length /@ subsets;
  And[
    DuplicateFreeQ[sums],
    AllTrue[Range[2, Length[set]], 
     With[{b = Select[len, # == #2 &], c = Select[len, # < #2 &]}, 
       AllTrue[Subsets[set, {#}], 
         Total[First[#]] > Total[Last[#]] &] & /@ 
        Tuples[{b, c}]] &]
  ]
]
findOptimumSet[n_] := Module[{bestSet, bestSum = Infinity, candidates, middle},
  If[n == 1, Return[{1}]];
  If[n == 2, Return[{1, 2}]];
  If[n == 3, Return[{2, 3, 4}]];
  If[n == 4, Return[{3, 5, 6, 7}]];
  If[n == 5, Return[{6, 9, 11, 12, 13}]];
  If[n == 6, Return[{11, 18, 19, 20, 22, 25}]];
  middle = findOptimumSet[n - 1][[Ceiling[(n - 1)/2]]];
  candidates = Subsets[Range[middle, middle + 2 n], {n}];
  Do[
    If[isSpecialSumSet[candidate] && Total[candidate] < bestSum,
      bestSet = candidate;
      bestSum = Total[candidate];
    ],
    {candidate, candidates}
  ];
  bestSet
]
StringJoin[ToString /@ findOptimumSet[7]]

