(* Define the ellipse equation and the line of the laser beam *)
ellipseEquation = 4 x^2 + y^2 == 100;
initialPoint = {0.0, 10.1};
initialDirection = {1.4, -19.7};

(* Function to find the next intersection point of the line with the ellipse *)
nextIntersection[{x0_, y0_}, {dx_, dy_}] := 
 Module[{t, x, y, sol},
  sol = Solve[{x == x0 + t dx, y == y0 + t dy, 
     ellipseEquation}, {x, y, t}, Reals];
  Select[{x, y} /. sol, 
    t /. sol[[#]] > 0 && 
      Norm[{x, y} /. sol[[#]] - {x0, y0}] > 10^-6 &][[1]]
  ]

(* Function to calculate the reflection direction *)
reflectDirection[{x0_, y0_}, {dx_, dy_}] := 
 Module[{nx, ny, norm, dot, rx, ry},
  {nx, ny} = D[{4 x^2 + y^2 - 100}, {{x, y}}] /. {x -> x0, y -> y0};
  norm = Sqrt[nx^2 + ny^2];
  {nx, ny} = {nx, ny}/norm;
  dot = 2 (dx nx + dy ny);
  {rx, ry} = {dx, dy} - dot {nx, ny};
  {rx, ry}
  ]

(* Simulate the laser beam path *)
beamPath[point_, direction_] := 
 Module[{p = point, d = direction, count = 0},
  While[!(Abs[p[[1]]] <= 0.01 && p[[2]] > 0),
   p = nextIntersection[p, d];
   d = reflectDirection[p, d];
   count++
   ];
  count
  ]

(* Calculate the number of bounces before exiting *)
beamPath[initialPoint, initialDirection]

