TriominoTilingCount[n_, m_] := Module[{dp, i, j, k, l, mask, newMask, count},
  dp = ConstantArray[0, {n + 1, 2^m}];
  dp[[1, 0]] = 1;
  For[i = 1, i <= n, i++,
   For[j = 0, j < 2^m, j++,
    If[dp[[i, j]] > 0,
     For[k = 0, k < 2^m, k++,
      mask = BitOr[j, k];
      If[BitCount[mask] == m,
       newMask = BitAnd[BitNot[mask], 2^m - 1];
       count = 1;
       For[l = 0, l < m, l++,
        If[BitGet[newMask, l] == 0,
         If[l < m - 1 && BitGet[newMask, l + 1] == 0,
          count *= 2;
          l++
          ,
          If[i < n && BitGet[j, l] == 0,
           count *= 2
           ,
           count = 0;
           Break[]
           ]
          ]
         ]
        ];
       dp[[i + 1, k]] += dp[[i, j]] * count
       ]
      ]
     ]
    ]
   ];
  dp[[n + 1, 0]]
  ]

