(* Generate the sequence of random numbers *)
s = NestList[Mod[#, 50515093] &, 290797, 20000];
t = Mod[s, 500];

(* Create the line segments *)
segments = Partition[Partition[t, 2], 2];

(* Function to check if two segments intersect at a true intersection point *)
trueIntersection[{p1_, p2_}, {q1_, q2_}] := Module[
  {det, det1, det2, t, u, intersection},
  det = (p2[[1]] - p1[[1]]) (q2[[2]] - q1[[2]]) - (p2[[2]] - p1[[2]]) (q2[[1]] - q1[[1]]);
  If[det == 0, Return[None]]; (* Parallel or collinear *)
  det1 = (q1[[1]] - p1[[1]]) (q2[[2]] - q1[[2]]) - (q1[[2]] - p1[[2]]) (q2[[1]] - q1[[1]]);
  det2 = (p2[[1]] - p1[[1]]) (q1[[2]] - p1[[2]]) - (p2[[2]] - p1[[2]]) (q1[[1]] - p1[[1]]);
  t = det1/det;
  u = det2/det;
  If[0 < t < 1 && 0 < u < 1,
    intersection = p1 + t (p2 - p1);
    Return[intersection],
    Return[None]
  ]
];

(* Find all true intersection points *)
intersections = Reap[
  Do[
    Do[
      intersection = trueIntersection[segments[[i]], segments[[j]]];
      If[intersection =!= None, Sow[intersection]],
      {j, i + 1, Length[segments]}
    ],
    {i, 1, Length[segments] - 1}
  ]
][[2, 1]];

(* Count distinct intersection points *)
Length[Union[intersections]]

