  k[r_] := 1/r;
  totalArea = Pi;
  uncoveredArea = totalArea - 3 Pi (1/3)^2;
  iterate[areas_, ks_] := Module[{newKs, newAreas},
    newKs = Join[ks, 2 Total[ks] - ks];
    newAreas = Join[areas, Pi (1/newKs)^2];
    {newAreas, newKs}
  ];
  Nest[iterate @@ # &, {3 Pi (1/3)^2, {3}}, n];
  uncoveredArea -= Total[First[%]];
  N[uncoveredArea/totalArea]
]
