N[r_] := Module[{count = 0, c = {r/4, r/4}, o = {0, 0}, b, ob, bc, oc, dotOBBC, dotOBOC, dotBCOC},
  Do[
    b = {x, y};
    ob = Norm[b - o];
    bc = Norm[b - c];
    oc = Norm[c - o];
    dotOBBC = (b - o).(b - c);
    dotOBOC = (b - o).(c - o);
