ClearAll[generateRows, isCompatible, countWalls];
generateRows[n_] := generateRows[n] = Module[{rows, addBrick},
  rows = {};
  addBrick[pos_, row_] := Which[
    pos == n, AppendTo[rows, row],
    pos < n, (addBrick[pos + 2, Append[row, pos + 2]];
              addBrick[pos + 3, Append[row, pos + 3]])];
  addBrick[0, {}];
  rows
];
isCompatible[row1_, row2_] := Intersection[row1, row2] == {};
countWalls[width_, height_] := Module[{rows, compatible, dp},
  rows = generateRows[width];
  compatible = Table[isCompatible[rows[[i]], rows[[j]]], {i, Length[rows]}, {j, Length[rows]}];
  dp = ConstantArray[0, {height + 1, Length[rows]}];
  dp[[1]] = 1;
  Do[
    Do[
      dp[[h + 1, j]] += dp[[h, i]] & /@ Flatten[Position[compatible[[i]], True]],
      {j, Length[rows]}
    ],
    {h, height}
  ];
  Total[dp[[-1]]]
];
