generateTours[n_] := Module[{board, moves, validMoveQ, dfs, start, end, result},
  board = ConstantArray[0, {4, n}];
  moves = {{1, 0}, {-1, 0}, {0, 1}, {0, -1}};
  validMoveQ[{x_, y_}] := 1 <= x <= 4 && 1 <= y <= n && board[[x, y]] == 0;
  dfs[pos_, visited_] := Module[{x, y, nextPos, count = 0},
    {x, y} = pos;
    If[visited == 4 n - 1 && pos == {4, 1}, Return[1]];
    board[[x, y]] = 1;
    Do[
      nextPos = pos + move;
      If[validMoveQ[nextPos],
        count += dfs[nextPos, visited + 1]
      ],
      {move, moves}
    ];
    board[[x, y]] = 0;
    count
  ];
  start = {1, 1};
  end = {4, 1};
  result = dfs[start, 0];
  result
]
