findSquareIndex[n_] := Module[{x, y, squares, index, area, nextSquare},
  squares = {{1, 0, 1}}; (* {x, y, side length} *)
  index = 1;
  While[index < n,
   {x, y, area} = squares[[-1]];
   nextSquare = 
    If[x + area <= 1/(y + area), {x + area, y, area}, {x, y + area, 
      1/(y + area) - x}];
   AppendTo[squares, nextSquare];
   index++
   ];
  {Count[squares[[All, 1]], _?(# < squares[[-1, 1]] &)], 
