(* Function to calculate the area of a polygon given its vertices *)
polygonArea[pts_] := 1/2 Abs[Total[Cross[#, RotateLeft[#]] &@pts]];

(* Function to check if a point is inside a polygon *)
pointInPolygonQ[pt_, poly_] := Module[{n = Length[poly], j, oddNodes = False},
  j = n;
  Do[
   If[(poly[[i, 2]] < pt[[2]] && poly[[j, 2]] >= pt[[2]]) || 
     (poly[[j, 2]] < pt[[2]] && poly[[i, 2]] >= pt[[2]]),
    If[poly[[i, 1]] + (pt[[2]] - poly[[i, 2]])/(poly[[j, 2]] - poly[[i, 2]]) (poly[[j, 1]] - poly[[i, 1]]) < pt[[1]],
     oddNodes = ! oddNodes
     ]
    ];
   j = i,
   {i, 1, n}
   ];
  oddNodes
  ];

(* Function to check if a polygon is a convex hole *)
isConvexHole[poly_, pts_] := 
 Module[{hull = ConvexHullMesh[poly], hullPts},
  hullPts = MeshCoordinates[hull];
  And[
   Length[hullPts] == Length[poly],
   AllTrue[Complement[pts, poly], ! pointInPolygonQ[#, poly] &]
   ]
  ];

(* Find all subsets of points that form convex holes and calculate their areas *)
maxArea = 0;
Do[
 If[isConvexHole[poly, points],
  maxArea = Max[maxArea, polygonArea[poly]]
  ],
 {poly, Subsets[points, {3, Length[points]}]}
];

