ClearAll[isIntegerSidedTriangle, bisectorIntersection, isValidTriangle, perimeterSum]
isIntegerSidedTriangle[a_, b_, c_] := a <= b <= c && a + b > c
bisectorIntersection[a_, b_, c_] := Module[{s, A, B, C, E, F, G},
  s = (a + b + c)/2;
  A = {0, 0}; B = {c, 0}; C = {(c^2 + a^2 - b^2)/(2 c), Sqrt[a^2 - ((c^2 + a^2 - b^2)/(2 c))^2]};
  E = (b A + c B)/(b + c);
  F = (a B + c C)/(a + c);
  G = (a A + b C)/(a + b);
  {E, F, G}
]
isValidTriangle[a_, b_, c_] := Module[{E, F, G, A, B, C},
  {E, F, G} = bisectorIntersection[a, b, c];
  A = {0, 0}; B = {c, 0}; C = {(c^2 + a^2 - b^2)/(2 c), Sqrt[a^2 - ((c^2 + a^2 - b^2)/(2 c))^2]};
  Norm[E - F] == Norm[F - G] == Norm[G - E]
]
perimeterSum[max_] := Total[Select[Flatten[Table[{a, b, c}, {c, 1, max}, {b, 1, c}, {a, 1, b}], 2],
  isIntegerSidedTriangle @@ # && isValidTriangle @@ # &][[All, 1]]]
