generateSequences[n_] := Module[{seqs, addBit},
  seqs = {{0}, {1}};
  addBit[seq_] := {Append[seq, 0], Append[seq, 1]};
  Nest[Flatten[addBit /@ #, 1] &, seqs, n - 1]
];
