  subseqs = Table[Take[seq, {i, i + n - 1}], {i, 1, Length[seq] - n + 1}];
  Length[Union[subseqs]] == 2^n
];
circularBinarySequences[n_] := Module[{seqs, validSeqs},
  seqs = generateSequences[2^n];
  validSeqs = Select[seqs, isValidSequence[#, n] &];
