(* Define the grid and possible moves *)
gridSize = 5;
lowerRow = Table[{i, 1}, {i, gridSize}];
upperRow = Table[{i, gridSize}, {i, gridSize}];
center = {3, 3};

(* Define the possible moves for each position *)
moves[{x_, y_}] := Select[
  {{x + 1, y}, {x - 1, y}, {x, y + 1}, {x, y - 1}},
  1 <= #[[1]] <= gridSize && 1 <= #[[2]] <= gridSize &
];

(* Define the state as {ant position, seeds carried, seeds placed} *)
initialState = {center, 0, 0};

(* Define the transition probabilities and expected steps *)
transition[state_] := Module[
  {pos, carried, placed, newStates, prob, newPos, newCarried, newPlaced},
  {pos, carried, placed} = state;
  newStates = Reap[
    Do[
      newPos = move;
      newCarried = carried;
      newPlaced = placed;
      If[MemberQ[lowerRow, newPos] && carried == 0,
        newCarried = 1;
      ];
      If[MemberQ[upperRow, newPos] && carried == 1,
        newCarried = 0;
        newPlaced++;
      ];
      Sow[{newPos, newCarried, newPlaced}],
      {move, moves[pos]}
    ]
  ][[2, 1]];
  prob = 1/Length[newStates];
  {prob, newStates}
];

(* Use dynamic programming to calculate expected steps *)
expectedSteps = Association[];
queue = {initialState};
While[Length[queue] > 0,
  state = First[queue];
  queue = Rest[queue];
  If[! KeyExistsQ[expectedSteps, state],
    {prob, newStates} = transition[state];
    If[state[[3]] == gridSize,
      expectedSteps[state] = 0,
      expectedSteps[state] = 1 + Total[
        prob (expectedSteps[#] /. expectedSteps -> 0) & /@ newStates
      ];
      queue = Join[queue, newStates];
    ];
  ];
];

(* Output the expected number of steps *)
