(* Define a function to calculate the number of non-crossing Eulerian cycles for given m and n *)
NonCrossingEulerianCycles[m_, n_] := Module[{dp, i, j},
  dp = Table[0, {i, 0, m}, {j, 0, n}];
  dp[[0, 0]] = 1;
  Do[
    dp[[i, j]] = If[i > 0, dp[[i - 1, j]], 0] + If[j > 0, dp[[i, j - 1]], 0] + 
      If[i > 0 && j > 0, dp[[i - 1, j - 1]], 0];
    , {i, 0, m}, {j, 0, n}];
  dp[[m, n]]
]

