isValidTriangle[a_, b_, c_] := Module[{s, area, r, d, e, f, g, h, i, j, k, m, n, x, y, z},
  s = (a + b + c)/2;
  area = Sqrt[s (s - a) (s - b) (s - c)];
  r = area/s;
  d = 2 r;
  e = a^2 + b^2 - c^2;
  f = a^2 + c^2 - b^2;
  g = b^2 + c^2 - a^2;
  h = Sqrt[e^2 + 4 a^2 b^2];
  i = Sqrt[f^2 + 4 a^2 c^2];
  j = Sqrt[g^2 + 4 b^2 c^2];
  k = (a^2 + b^2 - c^2)/(2 a b);
  m = (a^2 + c^2 - b^2)/(2 a c);
  n = (b^2 + c^2 - a^2)/(2 b c);
  x = (a^2 + b^2 - c^2)/(2 a b);
  y = (a^2 + c^2 - b^2)/(2 a c);
  z = (b^2 + c^2 - a^2)/(2 b c);
  If[Abs[k] < 1 && Abs[m] < 1 && Abs[n] < 1 && Abs[x] < 1 && Abs[y] < 1 && Abs[z] < 1,
    True,
    False
  ]
]
