ClearAll[simulateGame, updateMemoryLarry, updateMemoryRobin, expectedDifference]
simulateGame[turns_] := Module[{larryMemory = {}, robinMemory = {}, larryScore = 0, robinScore = 0, calledNumbers = RandomInteger[{1, 10}, turns]},
  Do[
    If[MemberQ[larryMemory, calledNumbers[[i]]], larryScore++, larryMemory = updateMemoryLarry[larryMemory, calledNumbers[[i]], calledNumbers[[1 ;; i - 1]]]];
    If[MemberQ[robinMemory, calledNumbers[[i]]], robinScore++, robinMemory = updateMemoryRobin[robinMemory, calledNumbers[[i]]]];
    , {i, turns}];
  Abs[larryScore - robinScore]
]
updateMemoryLarry[memory_, newNumber_, pastNumbers_] := Module[{updatedMemory = memory},
  If[Length[updatedMemory] < 5, AppendTo[updatedMemory, newNumber], 
   updatedMemory = Append[DeleteCases[updatedMemory, First[SortBy[updatedMemory, FirstPosition[pastNumbers, #] &]]], newNumber]];
  updatedMemory
]
updateMemoryRobin[memory_, newNumber_] := Module[{updatedMemory = memory},
  If[Length[updatedMemory] < 5, AppendTo[updatedMemory, newNumber], updatedMemory = Append[Rest[updatedMemory], newNumber]];
  updatedMemory
]
expectedDifference[turns_, simulations_] := N[Mean[Table[simulateGame[turns], {simulations}]], 8]
