  While[queue =!= {},
   {current, moves} = First[queue];
   queue = Rest[queue];
   {grid, empty, red} = current;
   If[red === target, Return[moves]];
   Do[
    newPos = empty + dir;
    If[1 <= newPos[[1]] <= n && 1 <= newPos[[2]] <= n,
     grid[[empty[[1]], empty[[2]]]] = grid[[newPos[[1]], newPos[[2]]]];
     grid[[newPos[[1]], newPos[[2]]]] = 0;
     If[! KeyExistsQ[visited, {grid, newPos, red}],
      AppendTo[queue, {{grid, newPos, If[red === newPos, empty, red]}, moves + 1}];
      visited[{grid, newPos, red}] = True;
      ];
     grid[[newPos[[1]], newPos[[2]]]] = grid[[empty[[1]], empty[[2]]]];
     grid[[empty[[1]], empty[[2]]]] = 0;
