transitionMatrix[n_] := Module[{mat = SparseArray[{}, {n, n}]},
  Do[
   If[i > 1, mat[[i, i - 1]] = 1/2];
   If[i < n, mat[[i, i + 1]] = 1/2];
   , {i, n}];
  mat
  ]
croakProbability[n_, croak_] := 
  If[isPrime[n], If[croak == "P", 2/3, 1/3], 
   If[croak == "P", 1/3, 2/3]]
sequenceProbability[seq_] := 
 Module[{n = 500, m = Length[seq], mat, prob, vec, i, j},
  mat = transitionMatrix[n];
  vec = ConstantArray[1/n, n];
  Do[
   prob = SparseArray[{}, {n}];
   Do[
    prob[[j]] = croakProbability[j, seq[[i]]];
    , {j, n}];
   vec = (vec.mat)*prob;
   , {i, m}];
  Total[vec]
  ]
Rationalize[sequenceProbability[Characters["PPPPNNPPPNPPNPN"]], 0]

