ClearAll[flip, solveGame]
flip[board_, {r_, c_}] := Module[{newBoard = board},
  newBoard[[r, All]] = 1 - newBoard[[r, All]];
  newBoard[[All, c]] = 1 - newBoard[[All, c]];
  newBoard[[r, c]] = 1 - newBoard[[r, c]];
  newBoard
]
solveGame[n_] := Module[{board, moves, target, queue, visited, current, pos},
  board = ConstantArray[1, {n, n}];
  target = ConstantArray[0, {n, n}];
  queue = {{board, 0}};
  visited = <|Flatten[board] -> True|>;
  While[Length[queue] > 0,
   {current, moves} = First[queue];
   queue = Rest[queue];
   If[current === target, Return[moves]];
   Do[
    pos = {i, j};
    newBoard = flip[current, pos];
    If[! KeyExistsQ[visited, Flatten[newBoard]],
     AppendTo[queue, {newBoard, moves + 1}];
     visited[Flatten[newBoard]] = True;
     ],
    {i, n}, {j, n}
    ];
   ];
  Infinity
]
