SphericalTriangleAngles[r_, a_, b_, c_] := Module[{A, B, C},
  A = ArcCos[(Cos[a/r] - Cos[b/r] Cos[c/r])/(Sin[b/r] Sin[c/r])];
  B = ArcCos[(Cos[b/r] - Cos[a/r] Cos[c/r])/(Sin[a/r] Sin[c/r])];
  C = ArcCos[(Cos[c/r] - Cos[a/r] Cos[b/r])/(Sin[a/r] Sin[b/r])];
  {A, B, C}
]

