(* Function to perform a move *)
move[config_] := Module[{pos = FirstPosition[config, _?(# >= 2 &)]},
  If[pos === Missing["NotFound"], Return[config]];
  pos = pos[[1]];
  config[[pos]] -= 2;
  If[pos > 1, config[[pos - 1]] += 1];
  If[pos < Length[config], config[[pos + 1]] += 1];
  config
  ]

(* Function to check if the game is finished *)
finishedQ[config_] := AllTrue[config, # <= 1 &]

(* Simulate the game *)
simulateGame[config_] := Module[{current = config},
  While[!finishedQ[current],
   current = move[current]
   ];
  current
  ]

