   If[perm[[1]] != "A",
    With[{steps = SolveMaximix[perm]},
     If[steps > maxSteps,
      maxSteps = steps;
      maxPerms = {perm},
      If[steps == maxSteps, AppendTo[maxPerms, perm]]
