ClearAll[isWinningPosition];
isWinningPosition[coins_, silverDollar_] := Module[{n = Length[coins], grundy},
  grundy = ConstantArray[0, n];
  For[i = 1, i <= n, i++,
   grundy[[i]] = If[i == 1, 0, Min[Complement[Range[0, Max[grundy[[1 ;; i - 1]]]], 
      Table[BitXor[grundy[[j]], grundy[[i - j]]], {j, 1, i - 1}]]]];
   ];
  grundy[[silverDollar]] != 0
  ];

