  While[Length[currentCoins] > 0,
   If[isWinningPosition[currentCoins, silverDollar],
    currentCoins = Rest[currentCoins],
    currentCoins = Rest[currentCoins];
    If[currentCoins == {}, Return[3 - currentPlayer]];
    ];
   currentPlayer = 3 - currentPlayer;
   ];
