GreatCircleDistance[{x1_, y1_, z1_}, {x2_, y2_, z2_}] := 
  ArcCos[(x1 x2 + y1 y2 + z1 z2)/(Sqrt[x1^2 + y1^2 + z1^2] Sqrt[x2^2 + y2^2 + z2^2])]

Risk[{x1_, y1_, z1_}, {x2_, y2_, z2_}, r_] := 
  (GreatCircleDistance[{x1, y1, z1}, {x2, y2, z2}]/(Pi r))^2

IntegerPointsOnSphere[r_] := 
  Select[Tuples[Range[-r, r], 3], Total[#^2] == r^2 &]

MinimalRisk[r_] := Module[{points, n, s, graph, dist},
  points = IntegerPointsOnSphere[r];
  n = First@Select[points, #[[3]] == r &];
  s = First@Select[points, #[[3]] == -r &];
  graph = Graph[points, 
    UndirectedEdge @@@ Subsets[points, {2}], 
    EdgeWeight -> (Risk[#[[1]], #[[2]], r] & /@ 
       EdgeList[Graph[points, UndirectedEdge @@@ Subsets[points, {2}]]])];
  dist = FindShortestPath[graph, n, s, Method -> "Dijkstra"];
  Total[Risk @@@ Partition[dist, 2, 1], 1]
]

