    If[! KeyExistsQ[floors[floor], 1], floors[floor][1] = n; Break[]];
    lastPerson = Max[Keys[floors[floor]]];
    If[perfectSquareQ[lastPerson + n],
     room = 1;
     While[KeyExistsQ[floors[floor], room], room++];
     floors[floor][room] = n;
     Break[]
     ];
    floor++
    ];
   If[floor == f && KeyExistsQ[floors[floor], r], Return[n]];
   n++
   ]
  ];

