(* Define the cubic Bezier curve function *)
BezierCurve[t_, {p0_, p1_, p2_, p3_}] := (1 - t)^3 p0 + 3 (1 - t)^2 t p1 + 3 (1 - t) t^2 p2 + t^3 p3

(* Define the points for the problem *)
p0 = {1, 0};
p1 = {1, 1};
p2 = {0, 1};
p3 = {0, 0};

(* Parametric equations for the Bezier curve *)
x[t_] := BezierCurve[t, {p0[[1]], p1[[1]], p2[[1]], p3[[1]]}]
y[t_] := BezierCurve[t, {p0[[2]], p1[[2]], p2[[2]], p3[[2]]}]

(* Derivatives for arc length calculation *)
dxdt = D[x[t], t];
dydt = D[y[t], t];

(* Arc length integral from 0 to 1 *)
arcLength = NIntegrate[Sqrt[dxdt^2 + dydt^2], {t, 0, 1}];

(* Calculate the area under the curve using the shoelace formula *)
area = 1/2 Abs[
   Total[Table[x[t] dydt - y[t] dxdt, {t, 0, 1, 0.001}]]];

(* Calculate the result as the difference between arc length and area *)
result = arcLength - area;
result

