pythagoreanTree[depth_] := Module[{drawTree, rotate, scale, translate, baseSquare, tree},
  rotate[angle_] := {{Cos[angle], -Sin[angle]}, {Sin[angle], Cos[angle]}};
  scale[s_] := {{s, 0}, {0, s}};
  translate[v_] := {{1, 0, v[[1]]}, {0, 1, v[[2]]}, {0, 0, 1}};
  baseSquare = {{0, 0}, {1, 0}, {1, 1}, {0, 1}};
  drawTree[square_, depth_] := Module[{p1, p2, p3, p4, triangle, leftSquare, rightSquare},
    If[depth == 0, Return[{square}]];
    {p1, p2, p3, p4} = square;
    triangle = {p3, p3 + {3/5, 4/5}, p3 + {0, 1}};
    leftSquare = {p3, triangle[[2]], triangle[[2]] + {0, 1}, p3 + {0, 1}};
    rightSquare = {triangle[[2]], triangle[[3]], triangle[[3]] + {1, 0}, triangle[[2]] + {1, 0}};
    Join[{square}, drawTree[leftSquare, depth - 1], drawTree[rightSquare, depth - 1]]
  ];
  tree = drawTree[baseSquare, depth];
  Length[tree]
]
