M[a_, b_, c_] := Module[{poly, gcd},
  poly = n^4 + a n^3 + b n^2 + c n;
  gcd = PolynomialGCD[poly, poly /. n -> n + 1];
  Abs[Coefficient[gcd, n, 0]]
]

