EulerPhiSum[n_, m_] := Module[{primes, phiSum, i, j, k, d, q, r, s, t, u, v, w, x, y, z},
  primes = FactorInteger[n][[All, 1]];
  phiSum = 0;
  For[i = 1, i <= m, i++,
    d = GCD[n, i];
    q = n/d;
    r = i/d;
    s = EulerPhi[q];
    t = 1;
    For[j = 1, j <= Length[primes], j++,
      u = primes[[j]];
      v = 0;
      w = q;
      While[Mod[w, u] == 0,
        w = w/u;
        v++
      ];
      x = 0;
      y = r;
      While[Mod[y, u] == 0,
        y = y/u;
        x++
      ];
      z = Min[v, x];
      t *= u^z
    ];
    phiSum += s*t
  ];
  phiSum
];
