Clear[InsideKernelQ];
InsideKernelQ[polygon_] := Module[{n = Length[polygon], angles, originVisible},
  angles = Table[ArcTan[polygon[[i, 1]], polygon[[i, 2]]], {i, n}];
  originVisible = Total[Differences[Sort[angles]]] == 2 Pi;
  originVisible
];

(* Generate all possible polygons with vertices in the range [-n, n] *)
Clear[GeneratePolygons];
GeneratePolygons[n_] := Module[{points, polygons},
  points = Tuples[Range[-n, n], 2];
  polygons = Subsets[points, {3, Length[points]}];
  Select[polygons, PolygonQ]
];

(* Check if a set of points forms a valid polygon *)
Clear[PolygonQ];
PolygonQ[points_] := Module[{n = Length[points], area},
  area = 1/2 Abs[Total[Table[points[[i, 1]] points[[i + 1, 2]] - points[[i + 1, 1]] points[[i, 2]], {i, n - 1}]]];
  area > 0 && !SelfIntersectingQ[points]
];

(* Check if a polygon is self-intersecting *)
Clear[SelfIntersectingQ];
SelfIntersectingQ[points_] := Module[{n = Length[points], segments, intersections},
  segments = Table[{points[[i]], points[[i + 1]]}, {i, n - 1}];
  intersections = Select[Subsets[segments, {2}], IntersectQ @@ # &];
  Length[intersections] > 0
];

(* Check if two line segments intersect *)
Clear[IntersectQ];
IntersectQ[{p1_, p2_}, {q1_, q2_}] := Module[{det, t, u},
  det = (p2[[1]] - p1[[1]]) (q2[[2]] - q1[[2]]) - (p2[[2]] - p1[[2]]) (q2[[1]] - q1[[1]]);
  If[det == 0, False,
    t = ((q1[[1]] - p1[[1]]) (q2[[2]] - q1[[2]]) - (q1[[2]] - p1[[2]]) (q2[[1]] - q1[[1]])) / det;
    u = ((q1[[1]] - p1[[1]]) (p2[[2]] - p1[[2]]) - (q1[[2]] - p1[[2]]) (p2[[1]] - p1[[1]])) / det;
    0 <= t <= 1 && 0 <= u <= 1
  ]
];

(* Calculate P(n) *)
