ClearAll[R, S, F, expectedProfit, optimalT, superRamvokProfit]
expectedProfit[d_, c_, t_] := Module[{p, q, e},
  p = 1/d;
  q = 1 - p;
  e = Sum[i*p*q^(i - 1), {i, 1, t}] + t*p*q^(t - 1);
  e - c*t
]
optimalT[d_, c_] := Module[{t = 1, maxProfit = -Infinity, profit},
  While[True,
   profit = expectedProfit[d, c, t];
   If[profit > maxProfit, maxProfit = profit, Break[]];
   t++
   ];
  t - 1
]
R[d_, c_] := expectedProfit[d, c, optimalT[d, c]]
superRamvokProfit[d_, c_] := Module[{faces = Range[d], totalProfit = 0, currentProfit},
  While[Length[faces] > 0,
   currentProfit = R[Length[faces], c];
   totalProfit += currentProfit;
   faces = DeleteCases[faces, RandomChoice[faces]]
   ];
  totalProfit
]
S[d_, c_] := superRamvokProfit[d, c]
F[n_] := Sum[S[d, c], {d, 4, n}, {c, 0, n}]
