integerTrianglesWithIntegralInradii[pMax_] := Module[
  {triangles, p, a, b, c, s, r, IA, IB, IC, L, S},
  triangles = {};
  For[p = 3, p <= pMax, p++,
   For[a = 1, a <= p/3, a++,
    For[b = a, b <= (p - a)/2, b++,
     c = p - a - b;
     If[a + b > c,
      s = p/2;
      r = Sqrt[(s - a) (s - b) (s - c)/s];
      IA = s - a;
      IB = s - b;
      IC = s - c;
      If[IntegerQ[IA] && IntegerQ[IB] && IntegerQ[IC],
       L = p + IA + IB + IC;
       AppendTo[triangles, L];
       ];
      ];
     ];
    ];
   ];
  S = Total[triangles];
  S
  ]

