ClearAll[Q, S, n, w, board, isSafe, placeQueens];
isSafe[board_, row_, col_, n_, w_] := 
 Module[{i, j, maxMove = n - 1 - w}, 
  For[i = 1, i <= col, i++, If[board[[row, i]] == 1, Return[False]]];
  For[i = row - 1, j = col - 1, i >= 1 && j >= 1 && col - j <= maxMove, 
   i--, j--, If[board[[i, j]] == 1, Return[False]]];
  For[i = row + 1, j = col - 1, i <= n && j >= 1 && col - j <= maxMove, 
   i++, j--, If[board[[i, j]] == 1, Return[False]]];
  True
  ];
placeQueens[board_, col_, n_, w_] := 
 If[col > n, 1, 
  Sum[If[isSafe[board, i, col, n, w], 
    board[[i, col]] = 1; 
    With[{result = placeQueens[board, col + 1, n, w]}, 
     board[[i, col]] = 0; result], 0], {i, 1, n}]];
Q[n_, w_] := 
 Module[{board = ConstantArray[0, {n, n}]}, placeQueens[board, 1, n, w]];
S[n_] := Sum[Q[n, w], {w, 0, n - 1}];
