triangleArea[{x1_, y1_}, {x2_, y2_}, {x3_, y3_}] := Abs[(x1 (y2 - y3) + x2 (y3 - y1) + x3 (y1 - y2))/2]
isIntegralArea[area_] := IntegerQ[area]
