      For[c = b, c <= maxArea, c++,
        area = triangleArea[{0, 0}, {a, 0}, {b, c}];
        If[isIntegralArea[area],
          For[x1 = 0, x1 <= a, x1++,
            For[y1 = 0, y1 <= c, y1++,
              For[x2 = 0, x2 <= b, x2++,
                For[y2 = 0, y2 <= c, y2++,
                  If[x1 == 0 && y1 == 0 || x2 == 0 && y2 == 0 || x1 == a && y1 == 0 || x2 == b && y2 == c, Continue[]];
                  p1 = {0, 0}; p2 = {a, 0}; p3 = {b, c};
                  p4 = {x1, y1}; p5 = {x2, y2};
                  t1 = triangleArea[p1, p4, p5];
                  t2 = triangleArea[p2, p4, p5];
                  t3 = triangleArea[p3, p4, p5];
                  q = area - t1 - t2 - t3;
                  If[isIntegralArea[t1] && isIntegralArea[t2] && isIntegralArea[t3] && isIntegralArea[q],
                    count++
                  ];
                ]
              ]
            ]
          ]
        ]
      ]
    ]
  ];
