]

(* Define a function to check if a triangle contains no other lattice points inside or on its edges *)
noLatticePointsInsideOrOnEdges[{a_, b_, c_}] := Module[{points, edgePoints},
  points = Select[Tuples[Range[Min[a, b, c] - 1, Max[a, b, c] + 1], 2], 
    RegionMember[Polygon[{a, b, c}], #] &];
  edgePoints = Join[
    Table[a + t (b - a), {t, 0, 1, 1/Max[Abs[b - a]]}],
    Table[b + t (c - b), {t, 0, 1, 1/Max[Abs[c - b]]}],
    Table[c + t (a - c), {t, 0, 1, 1/Max[Abs[a - c]]}]
  ];
  Complement[points, edgePoints] === {}
]

(* Define a function to find the maximum perimeter triangle with no lattice points inside or on edges *)
maxPerimeterTriangle[r_] := Module[{latticePoints, triangles, validTriangles, maxPerimeter, bestTriangle},
  latticePoints = Select[Tuples[Range[-r, r], 2], Norm[#] <= r &];
  triangles = Subsets[latticePoints, {3}];
  validTriangles = Select[triangles, noLatticePointsInsideOrOnEdges];
  maxPerimeter = 0;
  bestTriangle = {};
  Do[
    With[{perimeter = Total[Norm /@ Differences[Append[triangle, First[triangle]]]]},
      If[perimeter > maxPerimeter,
        maxPerimeter = perimeter;
        bestTriangle = triangle;
      ]
    ],
    {triangle, validTriangles}
  ];
  bestTriangle
]

(* Calculate T(r) for r = 10^7 *)
