ClearAll[simulateRace, countEvenPermutations, p]
simulateRace[n_, L_] := Module[{X, v, positions, bumps, order, bumpGraph},
  X = RandomReal[{0, 1}, n];
  v = -Log[X];
  positions = Table[40 (j - 1), {j, n}];
  bumps = ConstantArray[False, n];
  order = Range[n];
  bumpGraph = Graph[{}, VertexLabels -> "Name"];
  While[True,
   positions += v;
   If[Min[positions] >= L, Break[]];
   Do[
    If[!bumps[[j]] && !bumps[[j + 1]] && positions[[j]] >= positions[[j + 1]],
     bumps[[j]] = True;
     bumpGraph = EdgeAdd[bumpGraph, order[[j]] -> order[[j + 1]]];
     ],
    {j, n - 1}
    ];
   ];
  order = TopologicalSort[bumpGraph];
  If[Length[order] < n, order = Join[order, Complement[Range[n], order]]];
  order
  ]

countEvenPermutations[n_, L_, trials_] := Module[{evenCount = 0},
  Do[
   If[EvenQ[Signature[simulateRace[n, L]]], evenCount++],
   {trials}
   ];
  N[evenCount/trials]
  ]

