(* Define the function to simulate the game and calculate the result *)
ClearAll[simulateGame];
simulateGame[initialTokens_] := Module[{board, moveToken, discardToken, validMoves, result},
  board = initialTokens;
  moveToken[{x_, y_}, {dx_, dy_}] := {x + dx, y + dy};
  discardToken[{x_, y_}, {dx_, dy_}] := {x + dx, y + dy};
  validMoves[{x_, y_}] := Select[{{1, 0}, {-1, 0}, {0, 1}, {0, -1}}, 
    board[[x + #[[1]], y + #[[2]]]] > 0 &];
  result = 0;
  While[True,
    (* Find a token to move *)
    If[Length[Select[Position[board, _?(# > 0 &)], 
        validMoves[First[#]] =!= {} &]] == 0, Break[]];
    (* Choose a token T to move *)
    With[{pos = RandomChoice[Select[Position[board, _?(# > 0 &)], 
        validMoves[First[#]] =!= {} &]]},
      (* Select and discard a token D *)
      With[{discardDir = RandomChoice[validMoves[First[pos]]]},
        board[[Sequence @@ discardToken[First[pos], discardDir]]]--;
        (* Move T *)
        With[{moveDir = RandomChoice[{{1, 0}, {-1, 0}, {0, 1}, {0, -1}}]},
          board[[Sequence @@ moveToken[First[pos], moveDir]]]++
        ];
      ];
    ];
    result++;
  ];
  result
];

(* Initialize the board with a specific configuration *)
initialTokens = ConstantArray[0, {100, 100}];
initialTokens[[50, 50]] = 1; (* Start with one token at the center *)

(* Run the simulation and output the result *)
simulateGame[initialTokens]

