ClearAll[r, q, transitionMatrix, probabilityVector, steadyStateProbability, Pkm]
r[0] = 306;
r[n_] := r[n] = Mod[r[n - 1]^2, 10007];
q[i_, j_, m_] := Mod[r[i*m + j], 5];
transitionMatrix[k_, m_] := Module[{matrix, i, j, qij},
  matrix = ConstantArray[0, {k, k}];
  Do[
    Do[
      qij = q[i, j, m];
      Switch[qij,
        0, matrix[[i + 1, i + 1]] += 0,
        1, matrix[[i + 1, i + 1]] += 1,
        2, matrix[[i + 1, Mod[2*i, k] + 1]] += 1,
        3, matrix[[i + 1, Mod[i^2 + 1, k] + 1]] += 3,
        4, matrix[[i + 1, Mod[i + 1, k] + 1]] += 1
      ],
      {j, 0, m - 1}
    ],
    {i, 0, k - 1}
  ];
  matrix = matrix/(m*5);
  matrix
]
probabilityVector[k_] := Module[{vec},
  vec = ConstantArray[0, k];
  vec[[1]] = 1;
  vec
]
steadyStateProbability[k_, m_] := Module[{matrix, vec, eigensystem, index},
  matrix = transitionMatrix[k, m];
  vec = probabilityVector[k];
  eigensystem = Eigensystem[Transpose[matrix]];
  index = FirstPosition[eigensystem[[1]], 1];
  If[index === Missing["NotFound"], Return[0]];
  vec . Normalize[eigensystem[[2, index[[1]]]], Total]
]
Pkm[k_, m_] := 1 - steadyStateProbability[k, m]
