        If[valid[[l, 1, 1]] <= i,
          dp[i, j, m] += Sum[
            dp[i - valid[[l, 1, 1]], valid[[l, 2, 1]], valid[[l, 2, 2]]],
            {l, Length[valid]}
          ]
        ],
        {l, Length[valid]}
      ],
      {m, k}
    ],
    {j, k},
    {i, 1, n}
  ];
