 Module[{vars, sol, minVal},
  vars = Variables[{expr1, expr2}];
  sol = 
   FindInstance[expr1 == expr2 && And @@ Thread[vars >= 0], vars, 
    Integers, 1];
  If[sol === {}, 0, 
   minVal = Min[expr1, expr2] /. First[sol];
   minVal]
  ]

